import { useEffect, useState } from 'react';
import { getFacturas } from '../services/api';

export default function Dashboard() {
  const [facturas, setFacturas] = useState([]);

  useEffect(() => {
    getFacturas().then(res => setFacturas(res.data));
  }, []);

  return (
    <div className="space-y-6">
      <h1 className="text-2xl font-semibold text-gray-800">Facturas</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6">
        <h1 className="text-xl font-bold mb-4">Listado de Productos</h1>
      <table className="min-w-full bg-white shadow">
        <thead>
          <tr className="bg-gray-200 text-left">
            <th className="p-2">ID</th>
            <th className="p-2">Nombre</th>
            <th className="p-2">CÃ³digo</th>
            <th className="p-2">Cantidad</th>
            <th className="p-2">Unidad</th>
            <th className="p-2">Precio Unitario</th>
            <th className="p-2">Monto</th>
          </tr>
        </thead>
        <tbody>
          {productos.map(p => (
            <tr key={p.id} className="border-t">
              <td className="p-2">{p.id}</td>
              <td className="p-2">{p.nombre}</td>
              <td className="p-2">{p.codigo}</td>
              <td className="p-2">{p.cantidad}</td>
              <td className="p-2">{p.unidad}</td>
              <td className="p-2">{p.precio_unitario}</td>
              <td className="p-2">{p.monto}</td>
            </tr>
          ))}
        </tbody>
      </table>
      </div>
    </div>
  );
}
