import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import Dashboard from './pages/Dashboard';
import UploadXML from './pages/UploadXML';
import Productos from './pages/Productos';
import Facturas from './pages/Facturas';
import FacturaDetalle from './pages/FacturaDetalle';
import EditarProductos from './pages/EditarProductos';
import ProductoDetalle from './pages/EditarProductos';
import BusquedaProductos from './pages/BusquedaProductos';

export default function App() {
  return (
    <BrowserRouter>
      <div className="flex h-screen bg-gray-50 font-sans">
        <aside className="w-60 bg-white border-r shadow-sm">
          <div className="p-6 font-bold text-xl text-gray-800">Panel XML</div>
          <nav className="flex flex-col gap-2 px-6">
            <Link to="/" className="py-2 px-3 rounded hover:bg-gray-100 text-gray-700">Dashboard</Link>
            <Link to="/subir" className="py-2 px-3 rounded hover:bg-gray-100 text-gray-700">Subir XML</Link>
            <Link to="/leerProd" className="py-2 px-3 rounded hover:bg-gray-100 text-gray-700">Productos</Link>
            <Link to="/leerFact" className="py-2 px-3 rounded hover:bg-gray-100 text-gray-700">Facturas</Link>
|           <Route path="/productos/:id/dashboard" element={<DashboardProducto />} />


          </nav>
        </aside>
        <main className="flex-1 overflow-y-auto p-8">
          <Routes>
            <Route path="/" element={<Dashboard />} />
            <Route path="/subir" element={<UploadXML />} />
            <Route path="/leerProd" element={<Productos />} />
            <Route path="/leerFact" element={<Facturas />} />
            <Route path="/facturas/:id" element={<FacturaDetalle />} />
            <Route path="/productos/:id" element={<EditarProductos />} />

          </Routes>
        </main>
      </div>
    </BrowserRouter>
  );
}
