import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, LineElement, PointElement, CategoryScale, LinearScale, Tooltip, Legend } from 'chart.js';

ChartJS.register(LineElement, PointElement, CategoryScale, LinearScale, Tooltip, Legend);

export default function HistorialPrecioProducto({ historial }) {
  const data = {
    labels: historial.map(h => h.fecha), // ["2024-01", "2024-02", ...]
    datasets: [
      {
        label: 'Precio unitario ($)',
        data: historial.map(h => h.precio_unitario),
        borderColor: 'rgb(75,192,192)',
        tension: 0.3,
      },
    ],
  };

  return (
    <div className="p-4 bg-white shadow rounded mt-4">
      <h2 className="text-xl font-semibold mb-2">Historial de Precios</h2>
      <Line data={data} />
    </div>
  );
}
