import { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import HistorialPrecioProducto from './HistorialPrecioProducto';

export default function DashboardProducto() {
  const { id } = useParams();
  const [historial, setHistorial] = useState([]);

  useEffect(() => {
    const fetchHistorial = async () => {
      try {
        const res = await axios.get(`http://localhost:8001/productos/${id}/historial-precios`);
        setHistorial(res.data);
      } catch (error) {
        console.error("Error al obtener historial de precios:", error);
      }
    };

    fetchHistorial();
  }, [id]);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Dashboard Producto #{id}</h1>

      {/* Gráfico de historial */}
      <HistorialPrecioProducto historial={historial} />

      {/* Aquí puedes agregar más visualizaciones */}
    </div>
  );
}
