import { useEffect, useState } from 'react';
import axios from 'axios';

export default function BusquedaProductos() {
  const [query, setQuery] = useState('');
  const [codigoAdmin, setCodigoAdmin] = useState('');
  const [resultados, setResultados] = useState([]);

  const buscar = async () => {
    try {
      const res = await axios.get(`http://localhost:8001/productos/buscar-avanzado`, {
        params: { nombre: query, grupo_admin: codigoAdmin }
      });
      setResultados(res.data);
    } catch (error) {
      alert('Error al buscar productos');
    }
  };

  return (
    <div className="p-6 max-w-5xl mx-auto">
      <h1 className="text-2xl font-bold mb-6">Búsqueda de Productos</h1>

      <div className="flex gap-4 mb-6">
        <input
          type="text"
          placeholder="Nombre del producto"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="border p-2 rounded w-1/2"
        />
        <input
          type="text"
          placeholder="Código admin"
          value={codigoAdmin}
          onChange={(e) => setCodigoAdmin(e.target.value)}
          className="border p-2 rounded w-1/2"
        />
        <button onClick={buscar} className="bg-blue-600 text-white px-4 py-2 rounded">Buscar</button>
      </div>

      <table className="w-full border">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-3 border">Nombre</th>
            <th className="p-3 border">Proveedor</th>
            <th className="p-3 border">Precio</th>
            <th className="p-3 border">Fecha de Adquisición</th>
          </tr>
        </thead>
        <tbody>
          {resultados.map((p, idx) => (
            <tr key={idx} className="border-t">
              <td className="p-3 border">{p.nombre}</td>
              <td className="p-3 border">{p.proveedor?.nombre || 'N/A'}</td>
              <td className="p-3 border">{p.precio_unitario}</td>
              <td className="p-3 border">{p.fecha_emision}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
