import { useEffect, useState } from 'react';
import axios from 'axios';

export default function Productos() {
  const [productos, setProductos] = useState([]);
  const [categorias, setCategorias] = useState([]);

  useEffect(() => {
    fetchProductos();
    fetchCategorias();
  }, []);

  const fetchProductos = async () => {
    try {
      const res = await axios.get('http://localhost:8001/productos');
      setProductos(res.data);
    } catch (err) {
      console.error('Error al cargar productos:', err);
    }
  };

  const fetchCategorias = async () => {
    try {
      const res = await axios.get('http://localhost:8001/categorias');
      setCategorias(res.data);
    } catch (err) {
      console.error('Error al cargar categorías:', err);
    }
  };

  const handleEditar = async (id, nombreActual, categoriaActualId) => {
    const nuevoNombre = prompt('Nuevo nombre del producto:', nombreActual);
    const nuevaCategoriaId = prompt(
      `ID de nueva categoría para "${nuevoNombre || nombreActual}" (actual: ${categoriaActualId}):\n` +
      categorias.map(cat => `${cat.id} - ${cat.nombre}`).join('\n')
    );

    if (nuevoNombre && nuevaCategoriaId) {
      try {
        await axios.put(`http://localhost:8001/productos/${id}`, {
          nombre: nuevoNombre,
          categoria_id: parseInt(nuevaCategoriaId)
        });
        fetchProductos();
      } catch (err) {
        console.error('Error al editar producto:', err);
      }
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Productos</h1>
      <table className="min-w-full bg-white shadow rounded">
        <thead>
          <tr className="bg-gray-100">
            <th className="text-left p-3">ID</th>
            <th className="text-left p-3">Nombre</th>
            <th className="text-left p-3">Código</th>
            <th className="text-left p-3">Unidad</th>
            <th className="text-left p-3">Categoría</th>
            <th className="text-left p-3">Acciones</th>
          </tr>
        </thead>
        <tbody>
          {productos.map(p => (
            <tr key={p.id} className="border-t">
              <td className="p-3">{p.id}</td>
              <td className="p-3">{p.nombre}</td>
              <td className="p-3">{p.codigo}</td>
              <td className="p-3">{p.unidad}</td>
              <td className="p-3">{p.categoria?.nombre || 'Sin categoría'}</td>
              <td className="p-3">
                <button
                  onClick={() => handleEditar(p.id, p.nombre, p.categoria_id)}
                  className="bg-yellow-500 text-white px-3 py-1 rounded"
                >
                  Editar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
