import { useEffect, useState } from 'react';
import axios from 'axios';

export default function Productos() {
  const [productos, setProductos] = useState([]);
  const [nombre, setNombre] = useState('');
  const [codigo, setCodigo] = useState('');
  const [unidad, setUnidad] = useState('');

  const fetchProductos = async () => {
    const res = await axios.get('http://localhost:8001/productos');
    setProductos(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:8001/productos', {
      nombre,
      codigo,
      unidad
    });
    setNombre('');
    setCodigo('');
    setUnidad('');
    fetchProductos();
  };

  useEffect(() => {
    fetchProductos();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Productos</h1>

      <form onSubmit={handleSubmit} className="mb-6 space-y-4">
        <div className="grid grid-cols-3 gap-4">
          <input
            type="text"
            placeholder="Nombre"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
            className="border rounded p-2 w-full"
            required
          />
          <input
            type="text"
            placeholder="Código"
            value={codigo}
            onChange={(e) => setCodigo(e.target.value)}
            className="border rounded p-2 w-full"
            required
          />
          <input
            type="text"
            placeholder="Unidad"
            value={unidad}
            onChange={(e) => setUnidad(e.target.value)}
            className="border rounded p-2 w-full"
            required
          />
        </div>
        <button type="submit" className="bg-black text-white py-2 px-4 rounded">
          Agregar Producto
        </button>
      </form>

      <table className="min-w-full bg-white shadow rounded">
        <thead>
          <tr className="bg-gray-100">
            <th className="text-left p-3">ID</th>
            <th className="text-left p-3">Nombre</th>
            <th className="text-left p-3">Código</th>
            <th className="text-left p-3">Unidad</th>
            
          </tr>
        </thead>
        <tbody>
          {productos.map((p) => (
            <tr key={p.id} className="border-t">
              <td className="p-3">{p.id}</td>
              <td className="p-3">{p.nombre}</td>
              <td className="p-3">{p.codigo}</td>
              <td className="p-3">{p.unidad}</td>
              <td><Link to={`/productos/${producto.id}`} className="text-blue-600 hover:underline">
                Editar
              </Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
