import { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default function Productos() {
  const [productos, setProductos] = useState([]);
  const [nombreBusqueda, setNombreBusqueda] = useState('');
  const [proveedores, setProveedores] = useState([]);
  const [proveedorSeleccionado, setProveedorSeleccionado] = useState('');

  const fetchProductos = async () => {
    try {
      const res = await axios.get('http://localhost:8001/productos');
      setProductos(res.data);
    } catch (err) {
      console.error('Error al cargar productos', err);
    }
  };

  const fetchProveedores = async () => {
    try {
      const res = await axios.get('http://localhost:8001/proveedores');
      setProveedores(res.data);
    } catch (err) {
      console.error('Error al cargar proveedores', err);
    }
  };

  const buscarProductos = async () => {
    try {
      const queryParams = new URLSearchParams();
      if (nombreBusqueda) queryParams.append('nombre', nombreBusqueda);
      if (proveedorSeleccionado) queryParams.append('proveedor_id', proveedorSeleccionado);

      const res = await axios.get(`http://localhost:8001/productos/buscar-avanzado?${queryParams}`);
      setProductos(res.data);
    } catch (err) {
      console.error('Error en la bÃºsqueda', err);
    }
  };

  useEffect(() => {
    fetchProductos();
    fetchProveedores();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Productos</h1>

      <div className="mb-6 space-y-4">
        <div className="flex gap-4">
          <input
            type="text"
            placeholder="Buscar por nombre..."
            value={nombreBusqueda}
            onChange={(e) => setNombreBusqueda(e.target.value)}
            className="border rounded p-2 w-full"
          />
          <select
            value={proveedorSeleccionado}
            onChange={(e) => setProveedorSeleccionado(e.target.value)}
            className="border rounded p-2"
          >
            <option value="">Todos los proveedores</option>
            {proveedores.map((prov) => (
              <option key={prov.id} value={prov.id}>
                {prov.nombre}
              </option>
            ))}
          </select>
          <button onClick={buscarProductos} className="bg-black text-white px-4 rounded">
            Buscar
          </button>
        </div>
      </div>

      <table className="min-w-full bg-white shadow rounded">
        <thead className="bg-gray-100">
          <tr>
            <th className="p-3 text-left">Nombre</th>
            <th className="p-3 text-left">Proveedor</th>
            <th className="p-3 text-left">Precio Unitario</th>
            <th className="p-3 text-left">IVA</th>
            <th className="p-3 text-left">Otros Impuestos</th>
            <th className="p-3 text-left">Total</th>
          </tr>
        </thead>
        <tbody>
          {productos.map((p, i) => (
            <tr key={i} className="border-t">
              <td className="p-3">{p.producto}</td>
              <td className="p-3">{p.proveedor}</td>
              <td className="p-3">${p.precio_unitario?.toFixed(0)}</td>
              <td className="p-3">${p.iva?.toFixed(0)}</td>
              <td className="p-3">${p.otros_impuestos?.toFixed(0)}</td>
              <td className="p-3">${p.precio_unitario + (p.iva || 0) + (p.otros_impuestos || 0)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
