import { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Line, Bar, Radar
} from 'react-chartjs-2';
import {
  Chart as ChartJS, CategoryScale, LinearScale,
  PointElement, LineElement, Title, Tooltip, Legend, BarElement, RadialLinearScale
} from 'chart.js';

ChartJS.register(
  CategoryScale, LinearScale, PointElement,
  LineElement, Title, Tooltip, Legend, BarElement,
  RadialLinearScale
);

export default function DashboardInicio() {
  const [data, setData] = useState(null);

  useEffect(() => {
    const fetch = async () => {
      const res = await axios.get("http://localhost:8001/dashboard/principal");
      setData(res.data);
    };
    fetch();
  }, []);

  if (!data) return <div className="p-6">Cargando datos...</div>;

  const fechas = data.historial_precios.map(p => p.fecha.slice(0, 10));
  const precios = data.historial_precios.map(p => p.precio_promedio);

  const meses = data.facturas_mensuales.map(f => f.mes.slice(0, 7));
  const totales = data.facturas_mensuales.map(f => f.total);

  const proveedores = data.promedios_proveedor.map(p => p.proveedor);
  const promedios = data.promedios_proveedor.map(p => p.precio_promedio);
  const descargarExcel = async () => {
  try {
    const res = await axios.get("http://localhost:8001/exportar/productos/excel", {
      responseType: "blob"
    });
    const url = window.URL.createObjectURL(new Blob([res.data]));
    const link = document.createElement("a");
    link.href = url;
    link.setAttribute("download", "productos.xlsx");
    document.body.appendChild(link);
    link.click();
    link.remove();
  } catch (err) {
    console.error(err);
    alert("Error al descargar Excel");
  }
};

  return (
    <div className="p-6 space-y-10">
      <h1 className="text-2xl font-bold">Panel Principal</h1>

      <div>
        <h2 className="text-lg font-semibold mb-2">Historial de Precios Promedio</h2>
        <Line
          data={{
            labels: fechas,
            datasets: [{
              label: 'Precio Promedio',
              data: precios,
              fill: false,
              borderWidth: 2
            }]
          }}
        />
      </div>

      <div>
        <h2 className="text-lg font-semibold mb-2">Totales Mensuales de Facturas</h2>
        <Bar
          data={{
            labels: meses,
            datasets: [{
              label: 'Total por Mes',
              data: totales,
              backgroundColor: 'rgba(75, 192, 192, 0.5)',
            }]
          }}
        />
      </div>

      <div>
        <h2 className="text-lg font-semibold mb-2">Promedios por Proveedor</h2>
        <Radar
          data={{
            labels: proveedores,
            datasets: [{
              label: 'Precio Promedio',
              data: promedios
            }]
          }}
        />
      </div>
    </div>
  );
}
