import { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';



export default function ProductoDetalle() {
  const { id } = useParams();
  const [producto, setProducto] = useState(null);
  const [categorias, setCategorias] = useState([]);
  const [gruposAdmin, setGruposAdmin] = useState([]);
  const [categoriaSeleccionada, setCategoriaSeleccionada] = useState('');
  const [grupoSeleccionado, setGrupoSeleccionado] = useState('');

  useEffect(() => {
    const fetchProducto = async () => {
      const res = await axios.get(`http://localhost:8001/productos/${id}`);
      setProducto(res.data);
      setCategoriaSeleccionada(res.data.categoria?.id || '');
      setGrupoSeleccionado(res.data.grupo_admin?.id || '');
    };
    const fetchCategorias = async () => {
      const res = await axios.put(`http://localhost:8001/productos/${id}/asignar-categoria?categoria_id=${categoriaSeleccionada}`);
;
      setCategorias(res.data);
    };
    const fetchGruposAdmin = async () => {
      const res = await axios.put(`http://localhost:8001/productos/${id}/asignar-grupo?grupo_admin_id=${grupoSeleccionado}`);
;
      setGruposAdmin(res.data);
    };
    fetchProducto();
    fetchCategorias();
    fetchGruposAdmin();
  }, [id]);

  const asignarCategoria = async () => {
    try {
      await axios.put(`http://localhost:8001/productos/${id}/asignar-categoria`, {
        categoria_id: categoriaSeleccionada
      });
      alert('Categoría asignada correctamente');
    } catch (error) {
      alert('Error al asignar categoría');
    }
  };

  const asignarGrupoAdmin = async () => {
    try {
      await axios.put(`http://localhost:8001/productos/${id}/asignar-grupo`, {
        grupo_admin_id: grupoSeleccionado
      });
      alert('Código admin asignado correctamente');
    } catch (error) {
      alert('Error al asignar código admin');
    }
  };

  if (!producto) return <div className="p-6">Cargando...</div>;
 
  return (
    <div className="p-6 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-6">Detalle del Producto</h1>

      <div className="mb-4">
        <label className="block font-semibold">Nombre:</label>
        <div className="border p-2 rounded bg-gray-100">{producto.nombre}</div>
      </div>

      <div className="mb-4">
        <label className="block font-semibold">Código:</label>
        <div className="border p-2 rounded bg-gray-100">{producto.codigo}</div>
      </div>

      <div className="mb-4">
        <label className="block font-semibold">Proveedor:</label>
        <div className="border p-2 rounded bg-gray-100">{producto.proveedor?.nombre || 'N/A'}</div>
      </div>

      <div className="mb-4">
        <label className="block font-semibold">RUT Proveedor:</label>
        <div className="border p-2 rounded bg-gray-100">{producto.proveedor?.rut || 'N/A'}</div>
      </div>

      <div className="mb-4">
        <label className="block font-semibold">Cantidad:</label>
        <div className="border p-2 rounded bg-gray-100">{producto.cantidad || 'N/A'}</div>
      </div>

      <div className="mb-4">
        <label className="block font-semibold">Unidad:</label>
        <div className="border p-2 rounded bg-gray-100">{producto.unidad}</div>
      </div>

      <div className="mb-6">
        <label className="block font-semibold mb-1">Categoría:</label>
        <div className="flex items-center">
          <select
            value={categoriaSeleccionada}
            onChange={(e) => setCategoriaSeleccionada(e.target.value)}
            className="border p-2 rounded w-full"
          >
            <option value="">Seleccione una categoría</option>
            {categorias.map((c) => (
              <option key={c.id} value={c.id}>{c.nombre}</option>
            ))}
          </select>
          <button onClick={asignarCategoria} className="ml-2 bg-blue-600 text-white px-4 py-2 rounded">Asignar</button>
        </div>
      </div>

      <div className="mb-6">
        <label className="block font-semibold mb-1">Código Admin:</label>
        <div className="flex items-center">
          <select
            value={grupoSeleccionado}
            onChange={(e) => setGrupoSeleccionado(e.target.value)}
            className="border p-2 rounded w-full"
          >
            <option value="">Seleccione un código admin</option>
            {gruposAdmin.map((g) => (
              <option key={g.id} value={g.id}>{g.codigo}</option>
            ))}
          </select>
          <button onClick={asignarGrupoAdmin} className="ml-2 bg-blue-600 text-white px-4 py-2 rounded">Asignar</button>
        </div>
      </div>
    </div>
  );
}
