import { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default function Productos() {
  const [productos, setProductos] = useState([]);
  const [nombre, setNombre] = useState('');
  const [codigo, setCodigo] = useState('');
  const [unidad, setUnidad] = useState('');
  const [busqueda, setBusqueda] = useState('');

  const fetchProductos = async () => {
    try {
      const res = await axios.get('http://localhost:8001/productos');
      setProductos(res.data);
    } catch (err) {
      console.error('Error al cargar productos', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:8001/productos', {
      nombre,
      codigo,
      unidad
    });
    setNombre('');
    setCodigo('');
    setUnidad('');
    fetchProductos();
  };

  const handleBuscar = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.get(`http://localhost:8001/productos/buscar?nombre=${busqueda}`);
      setProductos(res.data);
    } catch (err) {
      console.error('Error al buscar productos', err);
    }
  };

  useEffect(() => {
    fetchProductos();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Productos</h1>

      {/* Formulario de búsqueda */}
      <form onSubmit={handleBuscar} className="mb-4 flex gap-2">
        <input
          type="text"
          placeholder="Buscar producto por nombre"
          value={busqueda}
          onChange={(e) => setBusqueda(e.target.value)}
          className="border rounded p-2 w-64"
        />
        <button type="submit" className="bg-blue-600 text-white py-2 px-4 rounded">
          Buscar
        </button>
        <button
          type="button"
          onClick={() => {
            setBusqueda('');
            fetchProductos();
          }}
          className="bg-gray-400 text-white py-2 px-4 rounded"
        >
          Limpiar
        </button>
      </form>

      {/* Formulario para agregar productos */}
      <form onSubmit={handleSubmit} className="mb-6 space-y-4">
        <div className="grid grid-cols-3 gap-4">
          <input
            type="text"
            placeholder="Nombre"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
            className="border rounded p-2 w-full"
            required
          />
          <input
            type="text"
            placeholder="Código"
            value={codigo}
            onChange={(e) => setCodigo(e.target.value)}
            className="border rounded p-2 w-full"
            required
          />
          <input
            type="text"
            placeholder="Unidad"
            value={unidad}
            onChange={(e) => setUnidad(e.target.value)}
            className="border rounded p-2 w-full"
            required
          />
        </div>
        <button type="submit" className="bg-black text-white py-2 px-4 rounded">
          Agregar Producto
        </button>
      </form>

      {/* Tabla de productos */}
      <table className="min-w-full bg-white shadow rounded">
        <thead>
          <tr className="bg-gray-100">
            <th className="text-left p-3">ID</th>
            <th className="text-left p-3">Nombre</th>
            <th className="text-left p-3">Código</th>
            <th className="text-left p-3">Unidad</th>
            <th className="text-left p-3">Acciones</th>
            <th className="text-left p-3">Área</th>
            <th className="text-left p-3">Cod. Admin</th>
            <th className="text-left p-3">Precio Unitario</th>
            <th className="text-left p-3">IVA</th>
            <th className="text-left p-3">Otros Impuestos</th>
            <th className="text-left p-3">Total</th>
          </tr>
        </thead>
        <tbody>
          {productos.map((p) => (
            <tr key={p.id} className="border-t">
              <td className="p-3">{p.id}</td>
              <td className="p-3">{p.nombre}</td>
              <td className="p-3">{p.codigo}</td>
              <td className="p-3">{p.unidad}</td>
              <td className="p-3">
                <Link to={`/productos/${p.id}`} className="text-blue-600 hover:underline">
                  Editar
                </Link>
              </td>
              <td className="p-3">{p.categoria?.nombre || 'Sin categoría'}</td>
              <td className="p-3">{p.grupo_admin?.codigo || 'Sin grupo'}</td>
              <td>{p.precio_unitario ? p.precio_unitario.toFixed(0) : '-'}</td>
              <td>{p.iva ? p.iva.toFixed(0) : '-'}</td>
              <td>{p.otros_impuestos ? p.otros_impuestos.toFixed(0) : '-'}</td>
              <td>{p.total ? p.total.toFixed(0) : '-'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
