import { useEffect, useState } from 'react';
import { getFacturas } from '../services/api';

export default function Dashboard() {
  const [facturas, setFacturas] = useState([]);

  useEffect(() => {
    getFacturas().then(res => setFacturas(res.data));
  }, []);

  return (
    <div className="space-y-6">
      <h1 className="text-2xl font-semibold text-gray-800">Facturas</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6">
        {facturas.map(f => (
          <div key={f.id} className="bg-white rounded-xl shadow p-5">
            <p className="text-gray-500 text-sm">Folio</p>
            <p className="text-lg font-medium text-gray-800">{f.folio}</p>
            <p className="mt-2 text-gray-600 text-sm">{f.razon_social_emisor}</p>
            <p className="text-sm text-gray-400">${f.monto_total}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
