import { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

export default function EditarProducto() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [producto, setProducto] = useState(null);
  const [codAdmins, setCodAdmins] = useState([]);
  const [codAdminId, setCodAdminId] = useState(null);
  const [nombre, setNombre] = useState("");
  const [precioUnitario, setPrecioUnitario] = useState("");

  useEffect(() => {
    // Traer producto
    axios.get(`/productos/${id}`).then((res) => {
      setProducto(res.data);
      setNombre(res.data.nombre);
      setPrecioUnitario(res.data.precio_unitario);
      setCodAdminId(res.data.cod_admin?.id || null);
    });

    // Traer lista de codigos admin para el select
    axios.get("/codigos_admin_maestro").then((res) => {
      setCodAdmins(res.data);
    });
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`/productos/${id}`, {
        nombre,
        precio_unitario: precioUnitario,
        cod_admin_id: codAdminId,
      });
      alert("Producto actualizado");
      navigate("/productos");
    } catch (error) {
      alert("Error al actualizar producto");
      console.error(error);
    }
  };

  if (!producto) return <div>Cargando...</div>;

  return (
    <form onSubmit={handleSubmit} className="p-4 max-w-lg mx-auto">
      <div>
        <label>Nombre:</label>
        <input
          type="text"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
          className="border p-2 w-full"
        />
      </div>

      <div className="mt-4">
        <label>Precio Unitario:</label>
        <input
          type="number"
          value={precioUnitario}
          onChange={(e) => setPrecioUnitario(parseFloat(e.target.value))}
          className="border p-2 w-full"
        />
      </div>

      <div className="mt-4">
        <label>Código Admin:</label>
        <select
          value={codAdminId || ""}
          onChange={(e) =>
            setCodAdminId(e.target.value === "" ? null : Number(e.target.value))
          }
          className="border p-2 w-full"
        >
          <option value="">-- Sin código admin --</option>
          {codAdmins.map((ca) => (
            <option key={ca.id} value={ca.id}>
              {ca.cod_admin} - {ca.nombre_producto}
            </option>
          ))}
        </select>
      </div>

      <button
        type="submit"
        className="mt-6 bg-blue-600 text-white px-4 py-2 rounded"
      >
        Guardar
      </button>
    </form>
  );
}
