import { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default function Productos() {
  const [productos, setProductos] = useState([]);
  const [nombre, setNombre] = useState('');
  const [grupoAdminId, setGrupoAdminId] = useState('');
  const [grupoAdmin, setGrupoAdmin] = useState([]);

  const [categorias, setCategorias] = useState([]);
  const [categoriaId, setCategoriaId] = useState('');
  const [fechaInicio, setFechaInicio] = useState('');
  const [fechaFin, setFechaFin] = useState('');

  useEffect(() => {
  fetchFiltros();
  fetchProductos();
}, []);

const fetchProductos = async () => {
  try {
    const res = await axios.get('http://localhost:8001/productos/filtrados', {
      params: {
        nombre: nombre || undefined,
        grupo_admin_id: grupoAdminId || undefined,
        categoria_id: categoriaId || undefined,
        fecha_inicio: fechaInicio || undefined,
        fecha_fin: fechaFin || undefined
      }
    });
    setProductos(res.data);
  } catch (err) {
    console.error('Error al obtener productos', err);
  }
};

const fetchFiltros = async () => {
  try {
    const resGrupo = await axios.get('http://localhost:8001/grupos_admin');
    const resCategorias = await axios.get('http://localhost:8001/categorias');
    setGrupoAdmin(resGrupo.data);
    setCategorias(resCategorias.data);
  } catch (err) {
    console.error('Error al obtener filtros', err);
  }
};



  const handleBuscar = (e) => {
    e.preventDefault();
    fetchProductos();
  };
 


  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Productos</h1>

     <form onSubmit={handleBuscar} className="mb-6 space-y-4">
      <div className="grid grid-cols-5 gap-4">
        <input
          type="text"
          placeholder="Buscar por nombre"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
          className="border rounded p-2 w-full"
        />
        <select
          value={grupoAdminId}
          onChange={(e) => setGrupoAdminId(e.target.value)}
          className="border rounded p-2"
        >
          <option value="">Grupo Admin</option>
          {grupoAdmin.map((g) => (
            <option key={g.id} value={g.id}>{g.codigo}</option>
          ))}
        </select>
        <select
          value={categoriaId}
          onChange={(e) => setCategoriaId(e.target.value)}
          className="border rounded p-2"
        >
          <option value="">Categoría</option>
          {categorias.map((c) => (
            <option key={c.id} value={c.id}>{c.nombre}</option>
          ))}
        </select>
        <input
          type="date"
          value={fechaInicio}
          onChange={(e) => setFechaInicio(e.target.value)}
          className="border rounded p-2"
        />
        <input
          type="date"
          value={fechaFin}
          onChange={(e) => setFechaFin(e.target.value)}
          className="border rounded p-2"
        />
      </div>
      <button type="submit" className="bg-black text-white py-2 px-4 rounded mt-4">
        Buscar
      </button>
    </form>


      <table className="min-w-full bg-white shadow rounded">
        <thead>
          <tr className="bg-gray-100">
            <th className="text-left p-3">ID</th>
            <th className="text-left p-3">Nombre</th>
            <th className="text-left p-3">Código</th>
            <th className="text-left p-3">Unidad</th>
            <th className="text-left p-3">Cantidad</th>
            <th className="text-left p-3">Imp. Adicional</th>
            <th className="text-left p-3">Área</th>
            <th className="text-left p-3">Cod. Admin</th>
            <th className="text-left p-3">Precio Unitario</th>

            <th className="text-left p-3">Total</th>
            <th className="text-left p-3">Acciones</th>
          </tr>
        </thead>
        <tbody>
          {productos.map((p) => (
            <tr key={p.id} className="border-t">
              <td className="p-3">{p.id}</td>
              <td className="p-3">{p.nombre}</td>
              <td className="p-3">{p.codigo}</td>
              <td className="p-3">{p.unidad}</td>
              <td className="p-3">{p.cantidad}</td>
              <td className="p-3">{p.imp_adicional?.toFixed(0) || 0}</td>
              <td className="p-3">{p.categoria?.nombre || 'Sin categoría'}</td>
              <td className="p-3">{p.grupo_admin?.codigo || 'Sin grupo'}</td>
              <td className="p-3">{p.precio_unitario?.toFixed(0) || '-'}</td>
          
              <td className="p-3">{p.total?.toFixed(0) || '-'}</td>
              <td className="p-3">
                <Link to={`/productos/${p.id}`} className="text-blue-600 hover:underline">
                  Editar
                </Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
